mapper.select:
  SELECT count(*)
	FROM PRODUCTS P JOIN SUPPLIERS S ON P.SUPPLIER_ID LIKE S.SUPPLIER_ID
					 		JOIN CATEGORIES C ON P.CATEGORY_ID LIKE C.CATEGORY_ID
							WHERE P.PRODUCT_NAME LIKE #{searchWord} 
							OR SUPPLIER_NAME LIKE #{searchWord} 
							OR ADDRESS LIKE #{searchWord} 
							OR CATEGORY_NAME LIKE #{searchWord}
